import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";
import amazon_produkte from "../.././products/amazon_produkte.json";
import christ_produkte from "../.././products/christ_produkte.json";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 80%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  height: 2850px;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 220px;
  height: 1450px;
  border: 1px solid black;
  margin-top: 60px;
  margin-left: 10px;
`;

const ProductDiv = styled.div`
  display: flex;
  background-color: white;
  flex-wrap: wrap;
  width: 210px;
  height: 400px;
  border: 0.1px solid black;
  margin-top: 10px;
  align-items: flex-start;
`;
const ProductImage = styled.div`
  display: flex;
  align-items: center;
  width: 210px;
  height: 330px;
  border: 1px solid black;
  img {
    max-width: 70%;
    height: auto;
    left: 0;
    right: 0;
    margin: 0px auto;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const NextPage = styled.div`
  overflow-y: hidden;
  display: flex;
  justify-content: space-between;
  width: 350px;
  height: 50px;
  border: 1px solid black;
  margin-top: 0px;
  margin-right: 15px;
  float: right;
`;
const NextPageIcons = styled.div`
  position: relative;
  width: 45px;
  height: 45px;
  button {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  h1 {
    position: absolute;
    text-align: center;
    font-size: 40px;
    top: -5px;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products() {
  const [prodPage, setProdPage] = useState(0);
  var allprods_past = [];
  var allprods = [];
  allprods = allprods_past.concat(amazon_produkte, christ_produkte);
  //console.log(allprods);
  //console.log(allprods[2].product_image);

  const allprods_display = useRef(shuffle(allprods));

  console.log(shuffle(allprods));
  return (
    <Router>
      <div>
        <MainDiv>
          <AddDiv></AddDiv>
          <StyledDiv>
            {allprods_display.current[prodPage].map((prod, index) => (
              <a href={prod.product_link}>
                <SingleProd
                  key={index}
                  id={index}
                  imgRes={prod.product_image_res[1]}
                  produkt={prod}
                  imgSize={imgSize(prod.product_image)}
                />
              </a>
            ))}
          </StyledDiv>
        </MainDiv>
        <NextPage>
          <NextPageIcons>
            <Link to="/seite 1">
              <button onClick={() => setProdPage(0)}>1</button>
            </Link>
          </NextPageIcons>
          <NextPageIcons>
            <button onClick={() => setProdPage(1)}>2</button>
          </NextPageIcons>
          <NextPageIcons>
            <button onClick={() => setProdPage(2)}>3</button>
          </NextPageIcons>
          <NextPageIcons>
            <h1>...</h1>
          </NextPageIcons>
          <NextPageIcons>
            <button>{allprods_display.current.length}</button>
          </NextPageIcons>
        </NextPage>
      </div>
    </Router>
  );
}

export default Products;

function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  for (b = 0; b <= d.length; b++) {
    c.push(d[b]);
    if (c.length == 12) {
      a.push(c);
      c = [];
    }
  }
  return a;
}

function imgSize() {}
