import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";
import amazon_produkte from "../../products/amazon_produkte.json";
import christ_produkte from "../../products/christ_produkte.json";

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 100%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 100%;
  height: 450px;
  border: 1px solid black;
  margin-top: 60px;

  @media only screen and (max-width: 420px) {
    pointer-events: none;
    display: none;
  }
`;

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const NextPage = styled.div`
  overflow-y: hidden;
  display: flex;
  justify-content: space-between;
  width: 350px;
  height: 50px;
  border: 1px solid black;
  margin-top: 10px;
  margin-right: 15px;
  float: right;
`;
const NextPageIcons = styled.div`
  position: relative;
  width: 45px;
  height: 45px;
  button {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  h1 {
    position: absolute;
    text-align: center;
    font-size: 40px;
    top: -5px;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products({ allprods_display, filter, filterSetts }) {
  const [prodPage, setProdPage] = useState(0);
  const tester = useRef([]);
  const allprodsShow = useRef([allprods_display]);
  const allproddCount = useRef(0);
  const allprods = useRef([]);
  //console.log(filterSetts);
  useEffect(() => {
    allprods.current.push(filterSetts);
    //console.log(filter);
    let cont = [...new Set(allprods.current)];
    if (cont.length > 2) {
      console.log(cont[cont.length - 1]);
      //console.log(cont[cont.length - 2]);
      if (cont[cont.length - 1][0] == cont[cont.length - 2][0]) {
        let check = [cont[cont.length - 1][3], cont[cont.length - 2][3]];
        check = check.flat();
        tester.current.push(check);
      } else {
        tester.current.push(cont[cont.length - 1][3]);
      }
    } else {
      tester.current.push(cont[cont.length - 1][3]);
    }
  }, [filter]);
  console.log(tester.current);
  return (
    <div>
      <AddDiv />
      <MainDiv>
        <StyledDiv>
          {allprodsShow.current[allproddCount.current][prodPage].map(
            (prod, index) => (
              <a href={prod.product_link}>
                <SingleProd
                  key={index}
                  id={index}
                  imgRes={prod.product_image_res[1]}
                  produkt={prod}
                />
              </a>
            )
          )}
        </StyledDiv>
      </MainDiv>
      <NextPage>
        <NextPageIcons>
          <button onClick={() => setProdPage(0)}>1</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(1)}>2</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(2)}>3</button>
        </NextPageIcons>
        <NextPageIcons>
          <h1>...</h1>
        </NextPageIcons>
        <NextPageIcons>
          <button>{allprodsShow.current[allproddCount.current].length}</button>
        </NextPageIcons>
      </NextPage>
    </div>
  );
}

export default Products;

const filterKategorie = (prods) => {
  var flatprods = prods.flat();
  let filter = [];
  let uniq = [];
  let endArr = [];
  uniq = ["Ohrring", "Ohrstecker", "Kette", "Ring", "Armband", "Creole"];
  endArr = [];
  uniq.map((item, index) => {
    flatprods.map((prct, ind) => {
      if (prct.product_keywords != null) {
        let prodKategorie = prct.product_keywords.toLowerCase();
        let savedKategorie = item.toLowerCase();
        if (prodKategorie.search(savedKategorie) != -1) {
          endArr.push(prct);
        }
      }
    });
    filter.push([item, endArr, false]);
    endArr = [];
  });
  return filter;
};
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////

const filterMarke = (prods) => {
  var flatprods = prods.flat();
  let filter = [];
  let uniq = [];
  let endArr = [];
  let markenArr = [];
  flatprods.map((prod, ind) => {
    if (prod.product_marke != null) {
      markenArr.push(prod.product_marke.toLowerCase());
    }
  });
  uniq = [...new Set(markenArr)];

  uniq.map((item, index) => {
    flatprods.map((prct, ind) => {
      if (prct.product_marke != null) {
        let prodMarke = prct.product_marke.toLowerCase();
        let savedMarke = item.toLowerCase();
        if (prodMarke.search(savedMarke) != -1) {
          endArr.push(prct);
        }
      }
    });
    filter.push([item, endArr, false]);
    endArr = [];
  });
  return filter;
};
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const filterMaterial = (prods) => {
  var flatprods = prods.flat();
  let filter = [];
  let uniq = [];
  let endArr = [];
  let materialArr = [];
  flatprods.map((prod, ind) => {
    if (prod.product_material != null) {
      materialArr.push(prod.product_material.toLowerCase());
    }
  });
  uniq = [...new Set(materialArr)];

  uniq.map((item, index) => {
    flatprods.map((prct, ind) => {
      if (prct.product_material != null) {
        let prodMaterial = prct.product_material.toLowerCase();
        let savedMaterial = item.toLowerCase();
        if (prodMaterial.search(savedMaterial) != -1) {
          endArr.push(prct);
        }
      }
    });
    filter.push([item, endArr, false]);
    endArr = [];
  });
  return filter;
};
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const filterFarbe = (prods) => {
  var flatprods = prods.flat();
  let filter = [];
  let uniq = [];
  let endArr = [];
  let farbenArr = [];
  flatprods.map((prod, ind) => {
    if (prod.product_farbe != null) {
      farbenArr.push(prod.product_farbe.toLowerCase());
    }
  });
  uniq = [...new Set(farbenArr)];

  uniq.map((item, index) => {
    flatprods.map((prct, ind) => {
      if (prct.product_farbe != null) {
        let prodFarbe = prct.product_farbe.toLowerCase();
        let savedFarbe = item.toLowerCase();
        if (prodFarbe.search(savedFarbe) != -1) {
          endArr.push(prct);
        }
      }
    });
    filter.push([item, endArr, false]);
    endArr = [];
  });
  return filter;
};
