import React, { useRef } from "react";
import styled from "styled-components";
import dropDownImg from "../../imgs/drop_down.png";
import amazonLogo from "../../imgs/amazonLogo.jpg";
import christLogo from "../../imgs/christLogo.png";
import { animated, useSpring, config, useSprings } from "react-spring";
import { easeSinInOut } from "d3-ease";
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  width: 90%;
  height: 350px;
  left: 0;
  right: 0;
  margin: auto;
  margin-top: 20px;
  border: 1px solid black;
`;
const StyledList = styled.ul`
  display: flex;
  width: 100%;
  height: 250px;
  justify-content: center;
  gap: 20px;
  align-items: center;
  list-style-type: none;
`;
const StyledListItems = styled.div`
  width: 165px;
  height: 80px;
  cursor: pointer;
  img {
    width: 100%;
    height: auto;
  }
`;
const FilterDiv = styled.div`
  width: 100%;
  max-height: 150px;
  display: flex;
  align-items: flex-start;
  gap: 30px;
  border: 1px solid black;
`;
const Filter = styled.div`
  display: flex;
  flex-direction: column;
  width: 120px;
`;
const FilterTopic = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 50px;
  border: 1px solid black;
  gap: 8px;
  span {
    font-size: 20px;
  }
  img {
    width: 12px;
    height: 10px;
    transform: rotate(180deg);
  }
`;
const FilterOpt = styled.div`
  display: none;
  pointer-events: none;
`;
const FilterOptDiv = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  overflow-y: scroll;
  padding-top: 10px;
  padding-bottom: 10px;
  width: 150px;
  background-color: #b3b3b3cc;
  height: 120px;
  border: 1px solid black;
  span {
    color: black;
    :hover {
      color: grey;
      cursor: pointer;
    }
  }
`;
const DeleteList = styled.div`
  background-color: #80808021;
  border: 1px solid black;
  display: flex;
  gap: 15px;
  width: 90%;
  right: 0;
  left: 0;
  margin: 140px auto;
`;
const DeleteListItem = styled.div`
  background-color: rgba(128, 128, 128, 0.47);
  width: 120px;
  height: 60px;
  display: flex;
  justify-content: center;
  cursor: pointer;
  align-items: center;
  h1 {
    font-size: 20px;
    text-align: center;
    font-weight: 300;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function SetProducts({
  deactProd,
  shopList,
  filter,
  setFilter,
  deleteList,
  filterlist,
}) {
  const [dropDown, setDropDown] = useSprings(4, (index) => ({
    transform: "rotate(0deg)",
  }));
  const [showOpt, setShowOpt] = useSprings(4, (index) => ({
    display: "none",
    pointerEvents: "none",
  }));

  const kategorieKeys = [];
  const materialKeys = [];
  const markenKeys = [];
  const farbenKeys = [];
  const keySetter = () => {
    filter[0].map((key, index) => {
      kategorieKeys.push(key);
    });
    filter[1].map((key, index) => {
      markenKeys.push(key);
    });
    filter[2].map((key, index) => {
      materialKeys.push(key);
    });
    filter[3].map((key, index) => {
      farbenKeys.push(key);
    });
    kategorieKeys.sort(function (a, b) {
      if (a > b) {
        return -1;
      }
      if (b > a) {
        return 1;
      }
      return 0;
    });
    markenKeys.sort(function (a, b) {
      if (a > b) {
        return -1;
      }
      if (b > a) {
        return 1;
      }
      return 0;
    });
    materialKeys.sort(function (a, b) {
      if (a > b) {
        return -1;
      }
      if (b > a) {
        return 1;
      }
      return 0;
    });
    farbenKeys.sort(function (a, b) {
      if (a > b) {
        return -1;
      }
      if (b > a) {
        return 1;
      }
      return 0;
    });
  };
  keySetter();
  const filtersetter = (n, m) => {
    var filterr = filter;
    let setter = [0, 0];
    let check = false;
    filterlist.map((filter, i) => {
      if (filter[2] == m) {
        setter[0] = false;
        check = true;
      }
    });
    if (check == false) {
      setter[0] = true;
    }
    filter[n].map((filter, i) => {
      if (filter[0] == m) {
        setter[1] = filterr[n][i][1];
      }
    });
    setFilter(filterr, n, m, setter[0], setter[1]);
  };

  //console.log(kategorieKeys);
  return (
    <div>
      <StyledDiv>
        <StyledList onClick={() => deactProd(1)}>
          <StyledListItems>
            <img src={amazonLogo} alt="Amazon" />
          </StyledListItems>
          <StyledListItems>
            <img src={christLogo} alt="Christ" />
          </StyledListItems>
        </StyledList>
        <FilterDiv>
          <Filter
            onMouseOver={() => {
              setDropDown.stop();
              setDropDown.start((index) =>
                index == 0
                  ? { transform: "rotate(180deg)" }
                  : { transform: "rotate(0deg)" }
              );
              setShowOpt.stop();
              setShowOpt.start((index) =>
                index == 0
                  ? { display: "initial", pointerEvents: "all" }
                  : { display: "none", pointerEvents: "none" }
              );
            }}
            onMouseOut={() => {
              setDropDown.stop();
              setDropDown.start((index) => ({ transform: "rotate(0deg)" }));
              setShowOpt.stop();
              setShowOpt.start((index) => ({
                display: "none",
                pointerEvents: "none",
              }));
            }}
          >
            <FilterTopic>
              <span>Kategorie</span>
              <animated.span style={dropDown[0]}>
                <img src={dropDownImg} alt="" />
              </animated.span>
            </FilterTopic>
            <FilterOpt as={animated.div} style={showOpt[0]}>
              <FilterOptDiv style={{ gap: "10px" }}>
                {kategorieKeys.map((key, index) => {
                  let newKey = key[0].charAt(0).toUpperCase() + key[0].slice(1);
                  return (
                    <span
                      style={{ fontWeight: 100 }}
                      onClick={() => filtersetter(0, key[0])}
                    >
                      {newKey} [{key[1].length}]
                    </span>
                  );
                })}
              </FilterOptDiv>
            </FilterOpt>
          </Filter>
          <Filter
            onMouseOver={() => {
              setDropDown.stop();
              setDropDown.start((index) =>
                index == 1
                  ? { transform: "rotate(180deg)" }
                  : { transform: "rotate(0deg)" }
              );
              setShowOpt.stop();
              setShowOpt.start((index) =>
                index == 1
                  ? { display: "initial", pointerEvents: "all" }
                  : { display: "none", pointerEvents: "none" }
              );
            }}
            onMouseOut={() => {
              setDropDown.stop();
              setDropDown.start((index) => ({ transform: "rotate(0deg)" }));
              setShowOpt.stop();
              setShowOpt.start((index) => ({
                display: "none",
                pointerEvents: "none",
              }));
            }}
          >
            <FilterTopic>
              <span>Marken</span>
              <animated.span style={dropDown[1]}>
                <img src={dropDownImg} alt="" />
              </animated.span>
            </FilterTopic>
            <FilterOpt as={animated.div} style={showOpt[1]}>
              <FilterOptDiv
                style={{ height: "200px", width: "250px", gap: "18px" }}
              >
                {markenKeys.map((key, index) => {
                  let newKey = key[0].charAt(0).toUpperCase() + key[0].slice(1);
                  return (
                    <span
                      style={{ fontWeight: 100 }}
                      onClick={() => filtersetter(1, key[0])}
                    >
                      {newKey} [{key[1].length}]
                    </span>
                  );
                })}
              </FilterOptDiv>
            </FilterOpt>
          </Filter>
          <Filter
            onMouseOver={() => {
              setDropDown.stop();
              setDropDown.start((index) =>
                index == 2
                  ? { transform: "rotate(180deg)" }
                  : { transform: "rotate(0deg)" }
              );
              setShowOpt.stop();
              setShowOpt.start((index) =>
                index == 2
                  ? { display: "initial", pointerEvents: "all" }
                  : { display: "none", pointerEvents: "none" }
              );
            }}
            onMouseOut={() => {
              setDropDown.stop();
              setDropDown.start((index) => ({ transform: "rotate(0deg)" }));
              setShowOpt.stop();
              setShowOpt.start((index) => ({
                display: "none",
                pointerEvents: "none",
              }));
            }}
          >
            <FilterTopic>
              <span>Material</span>
              <animated.span style={dropDown[2]}>
                <img src={dropDownImg} alt="" />
              </animated.span>
            </FilterTopic>
            <FilterOpt as={animated.div} style={showOpt[2]}>
              <FilterOptDiv
                style={{ height: "200px", width: "250px", gap: "18px" }}
              >
                {materialKeys.map((key, index) => {
                  let newKey = key[0].charAt(0).toUpperCase() + key[0].slice(1);
                  return (
                    <span
                      style={{ fontWeight: 100 }}
                      onClick={() => filtersetter(2, key[0])}
                    >
                      {newKey} [{key[1].length}]
                    </span>
                  );
                })}
              </FilterOptDiv>
            </FilterOpt>
          </Filter>
          <Filter
            onMouseOver={() => {
              setDropDown.stop();
              setDropDown.start((index) =>
                index == 3
                  ? { transform: "rotate(180deg)" }
                  : { transform: "rotate(0deg)" }
              );
              setShowOpt.stop();
              setShowOpt.start((index) =>
                index == 3
                  ? { display: "initial", pointerEvents: "all" }
                  : { display: "none", pointerEvents: "none" }
              );
            }}
            onMouseOut={() => {
              setDropDown.stop();
              setDropDown.start((index) => ({ transform: "rotate(0deg)" }));
              setShowOpt.stop();
              setShowOpt.start((index) => ({
                display: "none",
                pointerEvents: "none",
              }));
            }}
          >
            <FilterTopic>
              <span>Farben</span>
              <animated.span style={dropDown[3]}>
                <img src={dropDownImg} alt="" />
              </animated.span>
            </FilterTopic>
            <FilterOpt as={animated.div} style={showOpt[3]}>
              <FilterOptDiv
                style={{ height: "200px", width: "250px", gap: "18px" }}
              >
                {farbenKeys.map((key, index) => {
                  let newKey = key[0].charAt(0).toUpperCase() + key[0].slice(1);
                  return (
                    <span
                      style={{ fontWeight: 100 }}
                      onClick={() => filtersetter(3, key[0])}
                    >
                      {newKey} [{key[1].length}]
                    </span>
                  );
                })}
              </FilterOptDiv>
            </FilterOpt>
          </Filter>
        </FilterDiv>
      </StyledDiv>
      <DeleteList>
        {deleteList ? (
          deleteList.map((item, index) => (
            <DeleteListItem
              onClick={() => {
                filtersetter(item[1], item[2]);
              }}
            >
              <h1>{item[2]}</h1>
            </DeleteListItem>
          ))
        ) : (
          <div></div>
        )}
      </DeleteList>
    </div>
  );
}

