import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";
import amazon_produkte from "../../products/amazon_produkte.json";
import christ_produkte from "../../products/christ_produkte.json";

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 100%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 100%;
  height: 450px;
  border: 1px solid black;
  margin-top: 60px;

  @media only screen and (max-width: 420px) {
    pointer-events: none;
    display: none;
  }
`;

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const NextPage = styled.div`
  overflow-y: hidden;
  display: flex;
  justify-content: space-between;
  width: 350px;
  height: 50px;
  border: 1px solid black;
  margin-top: 10px;
  margin-right: 15px;
  float: right;
`;
const NextPageIcons = styled.div`
  position: relative;
  width: 45px;
  height: 45px;
  button {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  h1 {
    position: absolute;
    text-align: center;
    font-size: 40px;
    top: -5px;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products({ allprods_display, filter }) {
  const [prodPage, setProdPage] = useState(0);
  const newProds = useRef([]);
  var preProds = allprods_display;
  preProds = preProds.flat();
  //console.log(preProds);
  var filterTrue = [];
  var prods = [];
  filter.map((items, ind) => {
    Object.keys(items).map((item, index) => {
      if (index > 0) {
        if (items[item]) {
          filterTrue.push([items.filter, item]);
        }
      }
    });
  });
  const filterFiller = (filter) => {
    console.log(filter);
    for (var i = 0; i < filter.length; i++) {
      if (filter[i][0] == "material") {
        console.log("check material");
        let prodMaterial = [];
        preProds.map((product, index) => {
          if (newProds.current.length > 0) {
            prodMaterial = newProds.current;
          } else {
            prodMaterial = product.product_material;
          }
          if (prodMaterial != null) {
            prodMaterial = prodMaterial.toLowerCase();
            if (prodMaterial.search(filter[i][1]) != -1) {
              newProds.current.push(product);
            }
          }
        });
      }
      if (filter[i][0] == "marke") {
        console.log("check marke");
        let prodMarke = [];
        preProds.map((product, index) => {
          if (newProds.current.length > 0) {
            prodMarke = newProds.current;
          } else {
            prodMarke = product.product_marke;
          }
          if (prodMarke != null) {
            prodMarke = prodMarke.toLowerCase();
            if (prodMarke.search(filter[i][1]) != -1) {
              newProds.current.push(product);
            }
          }
        });
      }
      if (filter[i][0] == "kategorie") {
        console.log("check kategorie");
        let prodKategorie = [];
        preProds.map((product, index) => {
          if (newProds.current.length > 0) {
            prodKategorie = newProds.current;
          } else {
            prodKategorie = product.product_keywords;
          }
          if (prodKategorie != null) {
            prodKategorie = prodKategorie.toLowerCase();
            if (prodKategorie.search(filter[i][1]) != -1) {
              newProds.current.push(product);
            }
          }
        });
      }
      if (filter[i][0] == "farbe") {
        console.log("check farbe");
        preProds.map((product, index) => {
          if (newProds.current.length > 0) {
            prodKategorie = newProds.current;
          } else {
            prodFarbe = product.product_farbe;
          }
          let prodFarbe = product.product_farbe;
          if (prodFarbe != null) {
            if (prodFarbe.search(filter[i][1]) != -1) {
              newProds.current.push(product);
            }
          }
        });
      }
    }
  };
  if (filterTrue.length > 0) {
    filterFiller(filterTrue);
  } else {
  }
  prods = allprods_display;
  console.log(newProds.current);
  return (
    <div>
      <AddDiv />
      <MainDiv>
        <StyledDiv>
          {prods[prodPage].map((prod, index) => (
            <a href={prod.product_link}>
              <SingleProd
                key={index}
                id={index}
                imgRes={prod.product_image_res[1]}
                produkt={prod}
                imgSize={imgSize(prod.product_image)}
              />
            </a>
          ))}
        </StyledDiv>
      </MainDiv>
      <NextPage>
        <NextPageIcons>
          <button onClick={() => setProdPage(0)}>1</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(1)}>2</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(2)}>3</button>
        </NextPageIcons>
        <NextPageIcons>
          <h1>...</h1>
        </NextPageIcons>
        <NextPageIcons>
          <button>{prods.length}</button>
        </NextPageIcons>
      </NextPage>
    </div>
  );
}

export default Products;

function imgSize() {}
