import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { animated, useSpring, config } from "react-spring";
import leftArrow from "../../imgs/leftArrow.png";

const LikeDiv = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;

function LikeBar({ setOn }) {
  const [start, setStart] = useState(setOn);
  useEffect(() => {
    setStart(setOn);
  }, [setOn]);
  const [startStyle, setStartStyle] = useSpring(() => ({
    height: "0px",
    width: "0px",
    backgroundColor: "pink",
    position: "fixed",
    top: "30px",
    right: 0,
    zIndex: 99,
  }));
  const [arrowStyle, setArrowStyle] = useSpring(() => ({
    width: "90px",
    height: "90px",
    cursor: "pointer",
    marginLeft: "20px",
  }));
  if (start) {
    setStartStyle.stop();
    setStartStyle.start({ height: "1000px", width: "600px" });
  } else {
    setStartStyle.stop();
    setStartStyle.start({ height: "0px", width: "0px" });
  }
  return (
    <animated.div style={startStyle}>
      <LikeDiv>
        <div
          style={{
            display: "flex",
            width: "100%",
            height: "120px",
            border: "1px solid black",
          }}
        >
                  <animated.img src={leftArrow} alt="back" style={arrowStyle} onMouseOver={() => { setArrowStyle.stop();  setArrowStyle.start()} }/>
        </div>
      </LikeDiv>
    </animated.div>
  );
}

export default LikeBar;
