import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";

const ProductDiv = styled.div`
  display: flex;
  background-color: white;
  flex-wrap: wrap;
  width: 210px;
  height: 400px;
  border: 0.1px solid black;
  margin-top: 10px;
  align-items: flex-start;
`;
const ProductImage = styled.div`
  display: flex;
  align-items: center;
  width: 210px;
  height: 330px;
  border: 1px solid black;
  img {
    max-width: 70%;
    height: auto;
    left: 0;
    right: 0;
    margin: 0px auto;
  }
`;

function SingleProd({ imgHeight, produkt }) {
  return (
    <ProductDiv>
      <ProductImage>
        <img src={produkt.product_image} alt="" />
      </ProductImage>
    </ProductDiv>
  );
}

export default SingleProd;
