import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { animated, useSpring, config } from "react-spring";
import leftArrow from "../../imgs/leftArrow.png";

const LikeDiv = styled.div`
  width: 90%;
  height: 100%;
  display: flex;
  flex-direction: column;
  overflow-y: scroll;
`;

const SingleLikeDiv = styled.div`
  width: 100%;
  height: 165px;
  border: 1px solid black;
`;

function LikeBar({ setOn, setSideLike, likedProd }) {
  //console.log(likeList);
  const [startStyle, setStartStyle] = useSpring(() => ({
    height: "800px",
    width: "0px",
    backgroundColor: "pink",
    position: "fixed",
    top: "30px",
    right: 0,
    borderLeft: "1px solid grey",
    zIndex: 99,
  }));
  const [arrowStyle, setArrowStyle] = useSpring(() => ({
    width: "90px",
    height: "90px",
    cursor: "pointer",
    marginLeft: "20px",
    transform: "rotate(180deg)",
  }));
  if (setOn) {
    setStartStyle.stop();
    setStartStyle.start({ width: "600px" });
  } else {
    setStartStyle.stop();
    setStartStyle.start({ width: "0px" });
  }
  return (
    <animated.div style={startStyle}>
      <LikeDiv>
        <div
          style={{
            display: "flex",
            width: "100%",
            height: "200px",
          }}
        >
          <animated.img
            src={leftArrow}
            alt="back"
            style={arrowStyle}
            onMouseOver={() => {
              setArrowStyle.stop();
              setArrowStyle.start({ marginLeft: "30px" });
            }}
            onMouseOut={() => {
              setArrowStyle.stop();
              setArrowStyle.start({ marginLeft: "20px" });
            }}
            onClick={() => {
              setSideLike();
            }}
          />
        </div>
        {likedProd.map((prod, index) => (
          <SingleLikeDiv>{prod.product_name}</SingleLikeDiv>
        ))}
      </LikeDiv>
    </animated.div>
  );
}

export default LikeBar;
