import HomePage from "./components/pages/HomePage";
import ProduktePage from "./components/pages/ProduktePage";
import RatingPage from "./components/pages/RatingPage";
import ShopsPage from "./components/pages/ShopsPage";
import CouponPage from "./components/pages/CouponPage";
import InfoPage from "./components/pages/InfoPage";
import SearchedProd from "./components/pages/SearchedProd";
//////////////////////////////////////////
import AppHomePage from "./components/appV/appPages/AppHomePage";
import AppProduktePage from "./components/appV/appPages/AppProduktePage";
import AppRatingPage from "./components/appV/appPages/AppRatingPage";
import AppShopsPage from "./components/appV/appPages/AppShopsPage";
import AppCouponPage from "./components/appV/appPages/AppCouponPage";
import AppInfoPage from "./components/appV/appPages/AppInfoPage";
//////////////////////////////////////////
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import amazon_produkte from "./components/products/amazon_produkte.json";
import christ_produkte from "./components/products/christ_produkte.json";
import useWindowDimensions from "./components/hooks/useWindowDimensions";

function App() {
  const likeSave = useRef(JSON.parse(localStorage.getItem("likes")));
  const { height, width } = useWindowDimensions();
  const [passedProds, setPassedProds] = useState([]);
  const mobileV = useRef(false);
  var allprods_past = [];
  var allprods = [];
  var amazon = [];
  var christ = [];
  useEffect(()=>{},)

  const prodMaker = () => {
    amazon_produkte.map((prod, index) => {
      let p = prod.product_price.replace(/\./g, "");
      p = p.replace(/\,/g, "");
      if (p.length > 2) {
        p =
          p.substring(0, p.length - 2) +
          "." +
          p.substring(p.length - 2, p.length);
      }
      let obj = {
        product_root: prod.product_root,
        product_link: prod.product_link,
        product_name: prod.product_name,
        product_keywords: prod.product_keywords,
        product_price: p,
        product_image: prod.product_image,
        product_rating: prod.product_rating,
        product_image_res: prod.product_image_res,
        product_marke: prod.product_marke,
        product_material: prod.product_material
          .split(",")[0]
          .split(";")[0]
          .replace("-", " "),
        product_farbe: prod.product_farbe,
        product_Key: ["amazon", index, false],
      };
      amazon.push(obj);
    });

    christ_produkte.map((prod, index) => {
      let p = prod.product_price.replace(/\./g, "");
      p = p.replace(/\,/g, "");
      if (p.length > 2) {
        p =
          p.substring(0, p.length - 2) +
          "." +
          p.substring(p.length - 2, p.length);
      }
      let obj = {
        product_root: prod.product_root,
        product_link: prod.product_link,
        product_name: prod.product_name,
        product_keywords: prod.product_keywords,
        product_price: p,
        product_image: prod.product_image,
        product_rating: prod.product_rating,
        product_image_res: prod.product_image_res,
        product_marke: prod.product_marke,
        product_material: prod.product_material
          .split(",")[0]
          .split(";")[0]
          .replace("-", " "),
        product_farbe: prod.product_farbe,
        product_Key: ["christ", index, false],
      };
      christ.push(obj);
    });
  };
  prodMaker();
  const [product, setProduct] = useState([
    {
      id: 1,
      enabled: true,
      seite: "amazon",
      produkte: amazon,
    },
    {
      id: 2,
      enabled: true,
      seite: "christ",
      produkte: christ,
    },
  ]);

  product.map((shop, index) => {
    if (shop.enabled) {
      allprods_past.push(shop.produkte);
    }
  });

  allprods = allprods_past.flat();
  const [allprods_display, setAllprods_display] = useState([
    ...shuffle(allprods),
  ]);

  function deactivateProd(deactId) {
    var newObj = [...product];
    newObj.map((obj, index) => {
      if (deactId == obj.id) {
        obj["enabled"] = !obj.enabled;
      }
    });
    setProduct(newObj);
  }

  const addToFavs = (item) => {
    let newList = [];
    let check = false;
    let allLikes = JSON.parse(localStorage.getItem("likes"));
    if (allLikes != null) {
      if (allLikes.length < 1) {
        console.log("push");
        newList.push(item);
      } else {
        console.log("mehr");
        allLikes.map((likes, index) => {
          if (likes[0] == item[0] && likes[1] == item[1]) {
            check = true;
            console.log("check true");
          } else {
            newList.push(likes);
          }
        });
        if (!check) {
          newList.push(item);
        }
      }
    }
    if (allLikes == null) {
      console.log("null");
      newList.push(item);
    }
    localStorage.setItem("likes", JSON.stringify(newList));
    likeSave.current = JSON.parse(localStorage.getItem("likes"));
    //console.log(JSON.parse(localStorage.getItem("likes")));
    likesChecker()
  };

  //console.log(allprods_display);
  const likesChecker = () => {
    if (localStorage.getItem("likes") != null) {
      let likeList = JSON.parse(localStorage.getItem("likes"));
      likeSave.current = [...likeList];
      let check = false;
      let aProds = allprods_display.flat();
      //console.log(likeList);
      aProds.map((prod, index) => {
        check = false;
        likeList.map((like, ind) => {
          if (prod.product_Key[0] == like[0]) {
            if (prod.product_Key[1] == like[1]) {
              prod.product_Key[2] = true;
              check = true;
              return;
            }
          }
        });
        if (!check) {
          prod.product_Key[2] = false;
        }
      });
      setAllprods_display([...packer(aProds)]);
    }
  };
  if (width <= 1325) {
    mobileV.current = true;
  } else {
    mobileV.current = false;
  }
  return (
    <Router>
      {mobileV.current ? (
        <Routes>
          <Route path="/" exact element={<AppHomePage />} />
          <Route
            path="/schmuck_produkte"
            exact
            element={
              <AppProduktePage
                allprods_display={allprods_display}
                deactProd={deactivateProd}
                shopList={product}
              />
            }
          />
          <Route
            path="/top_bewertet"
            exact
            element={
              <AppRatingPage
                allprods_display={allprods_display}
                deactProd={deactivateProd}
                shopList={product}
              />
            }
          />
          <Route
            path="/all_shops"
            exact
            element={<AppShopsPage shopList={product} />}
          />
          <Route path="/coupons" exact element={<AppCouponPage />} />
          <Route path="/info" exact element={<AppInfoPage />} />
        </Routes>
      ) : (
        <Routes>
          <Route
            path="/"
            exact
            element={
              <HomePage
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
                allprods_display={allprods_display}
              />
            }
          />
          <Route
            path="/schmuck_produkte"
            exact
            element={
              <ProduktePage
                allprods_display={allprods_display}
                deactProd={deactivateProd}
                shopList={product}
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
                addToFavs={addToFavs}
              />
            }
          />
          <Route
            path="/top_bewertet"
            exact
            element={
              <RatingPage
                allprods_display={allprods_display}
                deactProd={deactivateProd}
                shopList={product}
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
              />
            }
          />
          <Route
            path="/all_shops"
            exact
            element={
              <ShopsPage
                shopList={product}
                allprods_display={allprods_display}
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
              />
            }
          />
          <Route
            path="/coupons"
            exact
            element={
              <CouponPage
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
                allprods_display={allprods_display}
              />
            }
          />
          <Route
            path="/info"
            exact
            element={
              <InfoPage
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
                allprods_display={allprods_display}
              />
            }
          />
          <Route
            path="/products/search"
            exact
            element={
              <SearchedProd
                allprods_display={allprods_display}
                allProds={passedProds}
                passProd={(prods) => {
                  setPassedProds([...prods]);
                }}
              />
            }
          ></Route>
        </Routes>
      )}
    </Router>
  );
}

export default App;

function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }

  var a = packer(d);

  return a;
}
function packer(d) {
  var b;
  var c = [];
  var a = [];
  var size = 55;
  for (var i = 0; i < d.length; i += size) {
    a.push(d.slice(i, i + size));
  }
  return a;
}
