  display: flex;
  width: 90%;
  height: 350px;
  left: 0;
  right: 0;
  margin: auto;
  margin-top: 20px;
  border: 1px solid black;
`;
const StyledList = styled.div`
  display: flex;
  width: 100%;
  height: 250px;
  justify-self: center;
  align-self: center;
  justify-content: center;
  gap: 20px;
  align-items: center;
  list-style-type: none;
`;

////////////////////////////////////////////////////////////////
