import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { animated, useSpring, config } from "react-spring";

const LikeDiv = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;

function LikeBar({ setOn }) {
  const [start, setStart] = useState(setOn);
  useEffect(() => {
    setStart(setOn);
  }, [setOn]);
  const startStyle = useSpring({
    to: { width: "600px", height: "1000px" },
    from: {
      height: "0px",
      width: "0px",
      backgroundColor: "pink",
      position: "fixed",
      top: "30px",
      right: 0,
      zIndex: 99,
    },
  });
  const endStyle = useSpring({
    to: { width: "0px", height: "0px" },
    from: {
      height: "600px",
      width: "1000px",
      backgroundColor: "pink",
      position: "fixed",
      top: "30px",
      right: 0,
      zIndex: 99,
    },
  });
  return (
    <animated.div style={start ? startStyle : endStyle}>
      <LikeDiv>
        <div
          style={{
            display: "flex",
            width: "100%",
            height: "120px",
            border: "1px solid black",
          }}
        ></div>
      </LikeDiv>
    </animated.div>
  );
}

export default LikeBar;
