import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";
import amazon_produkte from "../../products/amazon_produkte.json";
import christ_produkte from "../../products/christ_produkte.json";

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 100%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 100%;
  height: 450px;
  border: 1px solid black;
  margin-top: 60px;

  @media only screen and (max-width: 420px) {
    pointer-events: none;
    display: none;
  }
`;

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const NextPage = styled.div`
  overflow-y: hidden;
  display: flex;
  justify-content: space-between;
  width: 350px;
  height: 50px;
  border: 1px solid black;
  margin-top: 10px;
  margin-right: 15px;
  float: right;
`;
const NextPageIcons = styled.div`
  position: relative;
  width: 45px;
  height: 45px;
  button {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  h1 {
    position: absolute;
    text-align: center;
    font-size: 40px;
    top: -5px;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products({ allprods_display, filter }) {
  const [prodPage, setProdPage] = useState(0);
  var newProds = [];
  var allProds = []
  var preProds = allprods_display;
  const reMapKategorie = useRef(false)
  const reMapMarke = useRef(false)
  const reMapMetall = useRef(false)
  preProds = preProds.flat();
  //console.log(preProds);
  var filterTrue = [];
  var prods = [];
  filter.map((items, ind) => {
    Object.keys(items).map((item, index) => {
      if (index > 0) {
        if (items[item]) {
          filterTrue.push([items.filter, item]);
        }
      }
    });
  });
  const filterFiller = (filter) => {
    console.log(filter);
    for (var i = 0; i < filter.length; i++) {
      if (filter[i][0] == "kategorie") {
        console.log("check kategorie");
        preProds.map((product, index) => {
          if (product.product_keywords != null) {
            let prodKategorie = product.product_keywords.toLowerCase();
            if (prodKategorie.search(filter[i][1]) != -1) {
              newProds.push(product);
            }
          }
        });
      }
      if (filter[i][0] == "marke") {
        console.log("check marke");
        if (reMapKategorie.current) {
          newProds.map((prod, index)=>{})
        } else {
          preProds.map((product, index) => {
            if (product.product_marke != null) {
              let prodMarke = product.product_marke.toLowerCase();
              if (prodMarke.search(filter[i][1]) != -1) {
                newProds.push(product);
              }
            }
          });
        }
      }

      if (filter[i][0] == "material") {
        console.log("check material");
        preProds.map((product, index) => {
          if (product.product_material != null) {
            let prodMaterial = product.product_material.toLowerCase();
            if (prodMaterial.search(filter[i][1]) != -1) {
              newProds.push(product);
            }
          }
        });
      }

      if (filter[i][0] == "farbe") {
        console.log("check farbe");
        preProds.map((product, index) => {
          if (product.product_farbe != null) {
            let prodFarbe = product.product_farbe.toLowerCase();
            if (prodFarbe.search(filter[i][1]) != -1) {
              newProds.push(product);
            }
          }
        });
      }
    }
    allProds = newProds
    return allProds;
  };
  if (filterTrue.length > 0) {
    var size = 25;
    var past_prods = filterFiller(filterTrue);
    for (var i = 0; i < past_prods.length; i += size) {
      prods.push(past_prods.slice(i, i + size));
    }
  } else {
    prods = allprods_display;
  }

  console.log(newProds);
  return (
    <div>
      <AddDiv />
      <MainDiv>
        <StyledDiv>
          {prods[prodPage].map((prod, index) => (
            <a href={prod.product_link}>
              <SingleProd
                key={index}
                id={index}
                imgRes={prod.product_image_res[1]}
                produkt={prod}
                imgSize={imgSize(prod.product_image)}
              />
            </a>
          ))}
        </StyledDiv>
      </MainDiv>
      <NextPage>
        <NextPageIcons>
          <button onClick={() => setProdPage(0)}>1</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(1)}>2</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(2)}>3</button>
        </NextPageIcons>
        <NextPageIcons>
          <h1>...</h1>
        </NextPageIcons>
        <NextPageIcons>
          <button>{prods.length}</button>
        </NextPageIcons>
      </NextPage>
    </div>
  );
}

export default Products;

function imgSize() {}
