import HomePage from "./components/pages/HomePage";
import ProduktePage from "./components/pages/ProduktePage";
import RatingPage from "./components/pages/RatingPage";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import amazon_produkte from "./components/products/amazon_produkte.json";
import christ_produkte from "./components/products/christ_produkte.json";

function App() {
  var allprods_past = [];
  var allprods = [];
  const [product, setProduct] = useState([
    {
      id: 1,
      enabled: true,
      seite: "amazon",
      produkte: amazon_produkte,
    },
    {
      id: 2,
      enabled: false,
      seite: "christ",
      produkte: christ_produkte,
    },
  ]);

  product.map((shop, index)=>{shop.ena})
  for (var i = 0; i < product.length; i++) {
    if (product[i].enabled) {
      allprods_past.push(product[i].produkte);
    }
  }

  allprods = allprods_past.flat();

  const allprods_display = useRef(shuffle(allprods));

  function deactivateProd(deactId) {
    
      if (deactId == product[i].id) {
        if (product[i].enabled) {
          setProduct()
        } else {
        }
      }
    
  }

  return (
    <Router>
      <Routes>
        <Route path="/" exact element={<HomePage />} />
        <Route
          path="/schmuck_produkte"
          exact
          element={<ProduktePage allprods_display={allprods_display} />}
        />
        <Route
          path="/top_bewertet"
          exact
          element={<RatingPage allprods_display={allprods_display} />}
        />
        <Route path="/all_shops" exact element={<ProduktePage />} />
        <Route path="/gutscheine" exact element={<ProduktePage />} />
        <Route path="/info" exact element={<ProduktePage />} />
      </Routes>
    </Router>
  );
}

export default App;
function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  for (b = 0; b <= d.length; b++) {
    c.push(d[b]);
    if (c.length == 45) {
      a.push(c);
      c = [];
    }
  }
  return a;
}
