import React from "react";
import styled from "styled-components";
import dropDownImg from "../../imgs/drop_down.png";
import { animated, useSpring, config } from "react-spring";
import { easeSinInOut } from "d3-ease";
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  width: 90%;
  height: 350px;
  left: 0;
  right: 0;
  margin: auto;
  margin-top: 20px;
  border: 1px solid black;
`;
const StyledList = styled.ul`
  display: flex;
  width: 100%;
  height: 250px;
  justify-content: space-between;
  align-items: flex-start;
  list-style-type: none;
`;
const StyledListItems = styled.li`
  cursor: pointer;
`;
const FilterDiv = styled.div`
  width: 100%;
  height: 100px;
  display: flex;
  gap: 30px;
  border: 1px solid black;
`;
const Filter = styled.div`
  display: flex;
  flex-direction: column;
  width: 120px;
  height: 165px;
  border: 1px solid black;
`;
const FilterTopic = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 30%;
  border: 1px solid black;
  gap: 3px;
  img {
    width: 12px;
    height: 10px;
    transform: rotate(180deg);
  }
`;
const FilterOpt = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 10px;
  width: 100%;
  height: 70%;
  border: 1px solid black;
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function SetProducts({ deactProd }) {
  const [dropDown, setDropDown] = useSpring(() => ({
    transform: "rotate(0deg)",
  }));
  return (
    <StyledDiv>
      <StyledList onClick={() => deactProd(1)}>
        <StyledListItems>
          <a href="">a</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">b</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">c</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">d</a>
        </StyledListItems>
      </StyledList>
      <FilterDiv>
        <Filter>
          <FilterTopic>
            <span
              onMouseOver={() => {
                setDropDown.stop();
                setDropDown.start({ transform: "rotate(180deg)" });
              }}
              onMouseOut={() => {
                setDropDown.stop();
                setDropDown.start({ transform: "rotate(180deg)" });
              }}
            >
              Metall
            </span>
            <animated.span style={dropDown}>
              <img src={dropDownImg} alt="" />
            </animated.span>
          </FilterTopic>
          <FilterOpt>
            <span>Gold</span>
            <span>Silber</span>
            <span>Edelstahl</span>
          </FilterOpt>
        </Filter>
        <Filter></Filter>
      </FilterDiv>
    </StyledDiv>
  );
}

export default SetProducts;
