import HomePage from "./components/pages/HomePage";
import ProduktePage from "./components/pages/ProduktePage";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import amazon_produkte from "./components/products/amazon_produkte.json";
import christ_produkte from "./components/products/christ_produkte.json";

function App() {
  var allprods_past = [];
  var allprods = [];
  allprods = allprods_past.concat(amazon_produkte, christ_produkte);
  //console.log(allprods);
  //console.log(allprods[2].product_image);

  const allprods_display = useRef(shuffle(allprods));
  return (
    <Router>
      <Routes>
        <Route path="/" exact element={<HomePage />} />
        <Route path="/neuheiten" exact element={<ProduktePage />} />
        <Route path="/top_bewertet" exact element={<ProduktePage />} />
        <Route path="/schmuck_produkte" exact element={<ProduktePage />} />
        <Route path="/all_shops" exact element={<ProduktePage />} />
      </Routes>
    </Router>
  );
}

export default App;
function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  for (b = 0; b <= d.length; b++) {
    c.push(d[b]);
    if (c.length == 45) {
      a.push(c);
      c = [];
    }
  }
  return a;
}
