import React, { useRef } from "react";
import styled from "styled-components";
import dropDownImg from "../../imgs/drop_down.png";
import { animated, useSpring, config, useSprings } from "react-spring";
import { easeSinInOut } from "d3-ease";
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  width: 90%;
  height: 350px;
  left: 0;
  right: 0;
  margin: auto;
  margin-top: 20px;
  border: 1px solid black;
`;
const StyledList = styled.ul`
  display: flex;
  width: 100%;
  height: 250px;
  justify-content: space-between;
  align-items: flex-start;
  list-style-type: none;
`;
const StyledListItems = styled.li`
  cursor: pointer;
`;
const FilterDiv = styled.div`
  width: 100%;
  max-height: 150px;
  display: flex;
  align-items: flex-start;
  gap: 30px;
  border: 1px solid black;
`;
const Filter = styled.div`
  display: flex;
  flex-direction: column;
  width: 120px;
`;
const FilterTopic = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 50px;
  border: 1px solid black;
  gap: 8px;
  span {
    font-size: 20px;
  }
  img {
    width: 12px;
    height: 10px;
    transform: rotate(180deg);
  }
`;
const FilterOpt = styled.div`
  display: none;
  pointer-events: none;
`;
const FilterOptDiv = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  overflow-y: scroll;
  padding-top: 10px;
  padding-bottom: 10px;
  width: 150px;
  background-color: #b3b3b3cc;
  height: 120px;
  border: 1px solid black;
  span {
    color: black;
    :hover {
      color: grey;
      cursor: pointer;
    }
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function SetProducts({ deactProd, filters, setFilter }) {
  const [dropDown, setDropDown] = useSprings(4, (index) => ({
    transform: "rotate(0deg)",
  }));
  const [showOpt, setShowOpt] = useSprings(4, (index) => ({
    display: "none",
    pointerEvents: "none",
  }));

  const kategorieKeys = [];
  const materialKeys = [];
  const markenKeys = [];
  const farbenKeys = [];

  filters[0].map((key, index) => {
    kategorieKeys.push(key);
  });
  filters[1].map((key, index) => {
    markenKeys.push(key);
  });
  filters[2].map((key, index) => {
    materialKeys.push(key);
  });
  filters[3].map((key, index) => {
    farbenKeys.push(key);
  });
  kategorieKeys.sort(function (a, b) {
    if (a > b) {
      return -1;
    }
    if (b > a) {
      return 1;
    }
    return 0;
  });
  markenKeys.sort(function (a, b) {
    if (a > b) {
      return -1;
    }
    if (b > a) {
      return 1;
    }
    return 0;
  });
  materialKeys.sort(function (a, b) {
    if (a > b) {
      return -1;
    }
    if (b > a) {
      return 1;
    }
    return 0;
  });
  farbenKeys.sort(function (a, b) {
    if (a > b) {
      return -1;
    }
    if (b > a) {
      return 1;
    }
    return 0;
  });

  const filtersetter = (n, m) => {
    var filterr = filter;
    filters[n].map((filter, i) => {
      if (filters[0] == m) {
        filterr[n][i][2] = !filterr[n][i][2];
      }
    });
    setFilter(filterr);
  };

  console.log("kategorieKeys");
  return (
    <StyledDiv>
      <StyledList onClick={() => deactProd(1)}>
        <StyledListItems>
          <a href="">a</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">b</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">c</a>
        </StyledListItems>
        <StyledListItems>
          <a href="">d</a>
        </StyledListItems>
      </StyledList>
      <FilterDiv>
        <Filter
          onMouseOver={() => {
            setDropDown.stop();
            setDropDown.start((index) =>
              index == 0
                ? { transform: "rotate(180deg)" }
                : { transform: "rotate(0deg)" }
            );
            setShowOpt.stop();
            setShowOpt.start((index) =>
              index == 0
                ? { display: "initial", pointerEvents: "all" }
                : { display: "none", pointerEvents: "none" }
            );
          }}
          onMouseOut={() => {
            setDropDown.stop();
            setDropDown.start((index) => ({ transform: "rotate(0deg)" }));
            setShowOpt.stop();
            setShowOpt.start((index) => ({
              display: "none",
              pointerEvents: "none",
            }));
          }}
        >
          <FilterTopic>
            <span>Kategorie</span>
            <animated.span style={dropDown[0]}>
              <img src={dropDownImg} alt="" />
            </animated.span>
          </FilterTopic>
          <FilterOpt as={animated.div} style={showOpt[0]}>
            <FilterOptDiv style={{ gap: "10px" }}>
              {kategorieKeys.map((key, index) => {
                let newKey = key[0].charAt(0).toUpperCase() + key[0].slice(1);
                return (
                  <span
                    style={{ fontWeight: key[2] ? 700 : 100 }}
                    onClick={() => filtersetter(0, key[0])}
                  >
                    {newKey} [{key[1].length}]
                  </span>
                );
              })}
            </FilterOptDiv>
          </FilterOpt>
        </Filter>
        <Filter
          onMouseOver={() => {
