import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 100%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 220px;
  height: 1450px;
  border: 1px solid black;
  margin-top: 60px;
  margin-left: 10px;
  @media only screen and (max-width: 420px) {
    pointer-events: none;
    display: none;
  }
`;

const ProductDiv = styled.div`
  display: flex;
  background-color: white;
  flex-wrap: wrap;
  width: 210px;
  height: 400px;
  border: 0.1px solid black;
  margin-top: 10px;
  align-items: flex-start;
`;
const ProductImage = styled.div`
  display: flex;
  align-items: center;
  width: 210px;
  height: 330px;
  border: 1px solid black;
  img {
    max-width: 70%;
    height: auto;
    left: 0;
    right: 0;
    margin: 0px auto;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products({ allProds }) {
  const [prodsAll, setProdsAll] = useState(allProds[0]);
  useEffect(() => {
    setProdsAll([...allProds[0]]);
  }, [allProds]);
  console.log(allProds[1]);
  useEffect(() => {
    if (allProds[1] != null) {
      let editProds = [];
      prodsAll.map((prod, index) => {
        if (allProds[1].product_link != prod.product_link) {
          editProds.push(prod);
        }
      });
      editProds.unshift(allProds[1]);
      console.log(editProds);
    }
  }, []);
  //console.log(allprods);
  //console.log(allprods[2].product_image);

  //const allprods_display = useRef(shuffle(allprods));

  //console.log(shuffle(allprods));
  return (
    <div>
      <MainDiv>
        <StyledDiv>
          {prodsAll.map((prod, index) => {
            if (allProds[1] != null) {
              if (index != 0) {
                <a href={prod.product_link} style={{ textDecoration: "none" }}>
                  <SingleProd
                    key={index}
                    id={index}
                    imgRes={prod.product_image_res[1]}
                    produkt={prod}
                    imgSize={imgSize(prod.product_image)}
                    selected={false}
                  />
                </a>;
              } else {<a href={prod.product_link} style={{ textDecoration: "none" }}>
                <SingleProd
                  key={index}
                  id={index}
                  imgRes={prod.product_image_res[1]}
                  produkt={prod}
                  imgSize={imgSize(prod.product_image)}
                  selected={false}
                />
              </a>;
                
              }
            }
          })}
        </StyledDiv>
      </MainDiv>
    </div>
  );
}

export default Products;

function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  for (b = 0; b <= d.length; b++) {
    c.push(d[b]);
    if (c.length == 16) {
      a.push(c);
      c = [];
    }
  }
  return a;
}

function imgSize() {}
