import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import SingleProd from "./SingleProd";
import amazon_produkte from "../../products/amazon_produkte.json";
import christ_produkte from "../../products/christ_produkte.json";

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const StyledDiv = styled.div`
  display: flex;
  width: 100%;
  align-content: flex-start;
  justify-content: space-around;
  gap: 35px;
  float: right;
  right: 0;
  flex-wrap: wrap;
  margin-top: 35px;
`;
////////////////////////////////////////////////////////////////
const MainDiv = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
const AddDiv = styled.div`
  width: 100%;
  height: 450px;
  border: 1px solid black;
  margin-top: 60px;

  @media only screen and (max-width: 420px) {
    pointer-events: none;
    display: none;
  }
`;

////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
const NextPage = styled.div`
  overflow-y: hidden;
  display: flex;
  justify-content: space-between;
  width: 350px;
  height: 50px;
  border: 1px solid black;
  margin-top: 0px;
  margin-right: 15px;
  float: right;
`;
const NextPageIcons = styled.div`
  position: relative;
  width: 45px;
  height: 45px;
  button {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  h1 {
    position: absolute;
    text-align: center;
    font-size: 40px;
    top: -5px;
  }
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
function Products() {
  const [prodPage, setProdPage] = useState(0);
  var allprods_past = [];
  var allprods = [];
  allprods = allprods_past.concat(amazon_produkte, christ_produkte);
  //console.log(allprods);
  //console.log(allprods[2].product_image);

  const allprods_display = useRef(shuffle(allprods));

  //console.log(shuffle(allprods));
  return (
    <div>
      <AddDiv />
      <MainDiv>
        <StyledDiv>
          {allprods_display.current[prodPage].map((prod, index) => (
            <a href={prod.product_link}>
              <SingleProd
                key={index}
                id={index}
                imgRes={prod.product_image_res[1]}
                produkt={prod}
                imgSize={imgSize(prod.product_image)}
              />
            </a>
          ))}
        </StyledDiv>
      </MainDiv>
      <NextPage>
        <NextPageIcons>
          <button onClick={() => setProdPage(0)}>1</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(1)}>2</button>
        </NextPageIcons>
        <NextPageIcons>
          <button onClick={() => setProdPage(2)}>3</button>
        </NextPageIcons>
        <NextPageIcons>
          <h1>...</h1>
        </NextPageIcons>
        <NextPageIcons>
          <button>{allprods_display.current.length}</button>
        </NextPageIcons>
      </NextPage>
    </div>
  );
}

export default Products;

function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  for (b = 0; b <= d.length; b++) {
    c.push(d[b]);
    if (c.length == 30) {
      a.push(c);
      c = [];
    }
  }
  return a;
}

function imgSize() {}
