import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import ReactDOM from "react-dom";

const ProductDiv = styled.div`
  display: flex;
  background-color: white;
  flex-wrap: wrap;
  width: 210px;
  height: 400px;
  border: 0.1px solid black;
  margin-top: 10px;
  align-items: flex-start;
`;
const ProductImage = styled.div`
  display: flex;
  align-items: center;
  width: 210px;
  height: 330px;
  border: 1px solid black;
  img {
    width: 70%;
    height: auto;
    left: 0;
    right: 0;
    margin: 0px auto;
  }
`;
const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  width: 210px;
  height: 60px;
`;

function SingleProd({ id, imgHeight, produkt }) {
  const imgElement = useRef();
  var style;
  const [imgCheck, setImgCheck] = useState(1);

  if (imgCheck == 1) {
    style = { width: "140px" };
  } else if (imgCheck == 2) {
    style = { width: "180px" };
  } else {
    style = { width: "40px" };
  }
  console.log(imgCheck);
  return (
    <ProductDiv>
      <ProductImage>
        <img
          src={produkt.product_image}
          alt=""
          ref={imgElement}
          onLoad={() => {
            if (imgElement.current.clientHeight >= 100) {
              if (imgElement.current.clientHeight >= 400) {
                var num3 = 3;
                setImgCheck(num3);
              } else {
                var num1 = 1;
                setImgCheck(num1);
              }
            } else {
              var num2 = 2;
              setImgCheck(num2);
            }
          }}
          style={style}
        />
      </ProductImage>
      <ProductInfo>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <h5>{produkt.product_name} </h5>
            <h6>{produkt.product_keywords.slice(0, 35)}...</h6>
          </div>
          <h3>{produkt.product_price}â‚¬</h3>
        </div>
        <div>{produkt.product_root}</div>
      </ProductInfo>
    </ProductDiv>
  );
}

export default SingleProd;
