import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import likeHeart from "../../imgs/likeHeart.png";
import contactUs from "../../imgs/contactUs.png";
import searchIcon from "../../imgs/searchIcon.png";
import { Link } from "react-router-dom";
import { animated, useSpring, config } from "react-spring";
import { easeSinInOut, easeExpIn } from "d3-ease";
import useWindowDimensions from "../../hooks/useWindowDimensions";
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////

const NavDiv = styled.div`
  z-index: 99;
  position: fixed;
  display: flex;
  align-items: left;
  flex-direction: column;
  width: 100%;

  margin-top: 20px;
  border-top: 15px outset #e5e0e2;
`;
////////////////////////////////////////////////////////////////
const Logo = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  gap: 5px;
  padding: 6px;
  padding-left: 10px;
  background-color: #fedde6;
`;
const LogoSpan = styled(Link)`
  font-family: "Miama", sans-serif;
  color: black;
  font-size: 85px;
  text-decoration: none;
  list-style-type: none;
`;
////////////////////////////////////////////////////////////////
const NavList = styled.div`
  font-family: "PlayFair", sans-serif;
  display: flex;
  width: 100%;
  justify-content: space-between;
`;
////////////////////////////////
const NavListLeft = styled.ul`
  display: flex;
  width: 60%;
  height: 45px;
  gap: 5vw;
  padding-left: 5vw;
  background-color: #fedde6;
  /*
  border-bottom: 1px solid black;*/
  list-style-type: none;
`;
const StyledLink = styled(Link)`
  font-size: 20px;
  font-family: "PlayFair", sans-serif;
  color: black;
  padding-left: 10px;
  margin-top: 12px;
  cursor: pointer;
  text-decoration: none;
  list-style-type: none;
  /*filter: drop-shadow(0px 0px 25px #fee7eb);*/
  @media only screen and (max-width: 500px) {
    font-size: 10px;
  }
  :hover {
    color: rgba(0, 0, 0, 0.45);
  }
`;
////////////////////////////////
const NavListRight = styled.div`
  display: flex;
  justify-content: flex-start;
  width: 40%;
  height: 58px;
`;
const NavListRightSearchBar = styled.div`
  display: flex;
  width: 87%;
  height: 45px;
  background-color: #fedde6;
`;
const Searchimg = styled.img`
  position: absolute;
  right: 150px;
  width: 7%;
  height: 90%;
  opacity: 0.6;
  :hover {
    opacity: 1;
  }
`;
const NavlistRightIcons = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 16%;
  height: 58px;
  background-color: #fedde6;
`;
const BorderDiv = styled.div`
  width: 100%;
  height: 25%;
  /*border-left: 1px solid black;
  border-bottom: 1px solid black;*/
`;
const SearchPage = styled.div`
  position: absolute;
  z-index: 99;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #00000042;
`;
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////

function Navbar_coupons({ passProd }) {
  const searchSetter = useRef(false);
  const { height, width } = useWindowDimensions();
  const [inputText, setInput] = useState("");
  const [searchAnim, setSearchAnim] = useSpring(() => ({
    position: "absolute",
    top: "135px",
    left: "1200px",
    width: "700px",
    height: "40px",
    opacity: "1",
    transform: "scale(1)",
  }));
  const searchInput = useRef();
  const searchInputDiv = useRef();
  const [navShow, setNavShow] = useState(true);
  const [style, setStyle] = useSpring(() => ({
    opacity: 1,
    y: 0,
    pointerEvents: "all",
    config: { duration: 250, easing: easeSinInOut },
  }));
  const controllNav = () => {
    if (window.scrollY >= 1200) {
      setNavShow(false);
    } else {
      setNavShow(true);
    }
  };
  useEffect(() => {
    window.addEventListener("scroll", controllNav);
    return () => {
      window.removeEventListener("scroll", controllNav);
    };
  }, []);
  if (!navShow) {
    setStyle.stop();
    setStyle.start({ opacity: 0, y: -20, pointerEvents: "none" });
  } else {
    setStyle.stop();
    setStyle.start({ opacity: 1, y: 0, pointerEvents: "all" });
  }
  useEffect(() => {
    if (searchSetter.current) {
      console.log("check");
      setSearchAnim.stop();
      setSearchAnim.start({
        from: {
          x: 0,
          y: 0,
        },
        to: {
          x:
            -(width / 2 - searchInputDiv.current.offsetTop) +
            searchInputDiv.current.offsetWidth / 2,
          y:
            height / 2 -
            searchInputDiv.current.offsetTop -
            searchInputDiv.current.offsetHeight / 2,
          transform: "scale(2.6)",
        },
      });
    } else {
      setSearchAnim.stop();
      setSearchAnim.start({
        position: "absolute",
        top: 0,
        width: "700px",
        height: "40px",
        opacity: "1",
        x: 0,
        y: 0,
        transform: "scale(1)",
      });
    }
  }, [searchSetter.current]);
  //console.log(searchInputDiv.current.offsetTop);
  return (
    <NavDiv as={animated.div} style={style}>
      <SearchPage>
        <animated.div style={searchAnim} ref={searchInputDiv}>
          <textarea
            name="searchProd"
            cols="40"
            rows="1"
            ref={searchInput}
            style={{
              position: "absolute",
              height: "90%",
              resize: "none",
              backgroundColor: "#ffffff99",
              fontSize: "22px",
              fontFamily: "PlayFair, sans-serif",
              paddingLeft: " 10px",
              borderRadius: "50px",
            }}
            resize="false"
            placeholder="search..."
            onChange={(inp) => {
              setInput(inp.target.value);
              if (inp.target.value.length > 2) {
                searchSetter.current = true;
              } else if (inp.target.value.length == 0) {
                searchSetter.current = false;
              }
            }}
          />
          <Searchimg
            src={searchIcon}
            alt="icon"
            onClick={() => {
              searchInput.current.focus();
              console.log("click");
            }}
          />
        </animated.div>
      </SearchPage>
      <Logo>
        <div>
          <LogoSpan to="/">J</LogoSpan>
          <LogoSpan to="/">e</LogoSpan>
          <LogoSpan to="/">w</LogoSpan>
          <LogoSpan to="/">e</LogoSpan>
          <LogoSpan to="/">l</LogoSpan>
          <LogoSpan to="/">C</LogoSpan>
          <LogoSpan to="/">h</LogoSpan>
          <LogoSpan to="/">e</LogoSpan>
          <LogoSpan to="/">s</LogoSpan>
          <LogoSpan to="/">t</LogoSpan>
        </div>
      </Logo>
      <NavList>
        <NavListLeft>
          <StyledLink to="/schmuck_produkte">SCHMUCK</StyledLink>
          <StyledLink to="/top_bewertet">TOP BEWERTET</StyledLink>
          <StyledLink to="/all_shops">SHOPS</StyledLink>
          <StyledLink
            to="/coupons"
            style={{ fontWeight: "700", textDecoration: "underline" }}
          >
            COUPONS
          </StyledLink>
          <StyledLink to="/info">ÜBER UNS</StyledLink>
        </NavListLeft>
        <NavListRight>
          <NavListRightSearchBar />
          <NavlistRightIcons>
            <div
              style={{
                display: "flex",
                justifyItems: "flex-end",
                alignItems: "center",
                paddingRight: "10px",
                gap: "15px",
              }}
            >
              <img
                src={likeHeart}
                alt="cart"
                width="40px"
                height="40px"
                style={{ opacity: 0.7 }}
              />
              <img
                src={contactUs}
                alt="contactUs"
                width="40px"
                height="40px"
                style={{ opacity: 0.7 }}
              />
            </div>
          </NavlistRightIcons>
        </NavListRight>
      </NavList>
    </NavDiv>
  );
}

export default Navbar_coupons;
