import HomePage from "./components/pages/HomePage";
import ProduktePage from "./components/pages/ProduktePage";
import RatingPage from "./components/pages/RatingPage";
import ShopsPage from "./components/pages/ShopsPage";
import CouponPage from "./components/pages/CouponPage";
import InfoPage from "./components/pages/InfoPage";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import amazon_produkte from "./components/products/amazon_produkte.json";
import christ_produkte from "./components/products/christ_produkte.json";

function App() {
  var allprods_past = [];
  var allprods = [];
  var amazon = [];
  var crist = [];

  const prodMaker = () => {
    amazon_produkte.map((prod, index)=>{let obj = {
      product_root: "Amazon",
      product_link:
        "https://www.amazon.de/CDE-Embellished-Geschenkbox-Mutterstag-Valentinstag/dp/B07P9CKHCG/ref=sr_1_17_sspa?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=QRRIYW4AU9EF&keywords=damen+schmuck&qid=1644251879&sprefix=damen+schmuck%2Caps%2C111&sr=8-17-spons&psc=1&spLa=ZW5jcnlwdGVkUXVhbGlmaWVyPUExR0tDSUM4WDhLVlhWJmVuY3J5cHRlZElkPUEwMTQ4OTI1TDE4QUFMSUgzSjA5JmVuY3J5cHRlZEFkSWQ9QTA1NTk1NjgyT09IMlJHQjYyMU1OJndpZGdldE5hbWU9c3BfbXRmJmFjdGlvbj1jbGlja1JlZGlyZWN0JmRvTm90TG9nQ2xpY2s9dHJ1ZQ==",
      product_name: "CDE",
      product_keywords:
        "CDE Kette Damen Rosegold Herz Halskette für Frauen Schmuck mit Elegante Geschenkverpackung Geschenk für Sie Frau Freundin Mama Valentinstag/Party/Jubiläum/Geburtstag/Weihnachten",
      product_price: "59,99",
      product_image:
        "https://m.media-amazon.com/images/I/71dRxDVSDsL._AC_UL320_.jpg",
      product_rating: "4,5",
      product_image_res: [15228, [229, 320]],
      product_marke: "CDE",
      product_material: "Platin",
      product_farbe: null,
    };})
  };
  const [product, setProduct] = useState([
    {
      id: 1,
      enabled: true,
      seite: "amazon",
      produkte: amazon_produkte,
    },
    {
      id: 2,
      enabled: true,
      seite: "christ",
      produkte: christ_produkte,
    },
  ]);

  product.map((shop, index) => {
    if (shop.enabled) {
      allprods_past.push(shop.produkte);
    }
  });

  allprods = allprods_past.flat();

  const allprods_display = shuffle(allprods);

  function deactivateProd(deactId) {
    var newObj = [...product];
    newObj.map((obj, index) => {
      if (deactId == obj.id) {
        obj["enabled"] = !obj.enabled;
      }
    });
    setProduct(newObj);
  }
  return (
    <Router>
      <Routes>
        <Route path="/" exact element={<HomePage />} />
        <Route
          path="/schmuck_produkte"
          exact
          element={
            <ProduktePage
              allprods_display={allprods_display}
              deactProd={deactivateProd}
            />
          }
        />
        <Route
          path="/top_bewertet"
          exact
          element={<RatingPage allprods_display={allprods_display} />}
        />
        <Route path="/all_shops" exact element={<ShopsPage />} />
        <Route path="/coupons" exact element={<CouponPage />} />
        <Route path="/info" exact element={<InfoPage />} />
      </Routes>
    </Router>
  );
}

export default App;

function shuffle(d) {
  var j, x, i;
  for (i = d.length - 1; i > 0; i--) {
    j = Math.floor(Math.random() * (i + 1));
    x = d[i];
    d[i] = d[j];
    d[j] = x;
  }
  var b;
  var c = [];
  var a = [];
  var size = 25;
  for (var i = 0; i < d.length; i += size) {
    a.push(d.slice(i, i + size));
  }
  return a;
}
