import React from "react";
import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
import ReactDOM from "react-dom";
import startIcon from "../../imgs/star.png";

const ProductDiv = styled.div`
  font-family: "PlayFair", sans-serif;
  font-weight: 100;
  display: flex;
  background-color: white;
  width: 16vw;
  flex-direction: column;
  flex-wrap: wrap;
  height: 300px;
  border-bottom: 0.1px solid black;
  margin-top: 10px;
  align-items: flex-start;
`;
const ProductImage = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  height: 240px;

  img {
    width: 70%;
    height: auto;
    left: 0;
    right: 0;
    margin: 0px auto;
  }
`;
const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  color: black;
  width: 100%;
  height: 30px;
`;

function SingleProd({ id, imgRes, produkt }) {
  var style;
  if (imgRes[0] <= imgRes[1]) {
    style = { width: "auto", height: "12vw" };
  }
  if (imgRes[1] <= imgRes[0]) {
    style = { width: "12vw", height: "auto" };
  }
  if (imgRes[0] == imgRes[1]) {
    style = { width: "12vw", height: "240px" };
  }

  return (
    <ProductDiv style={{ overflow: "hidden" }}>
      <ProductImage>
        <img src={produkt.product_image} alt="" style={style} />
      </ProductImage>
      <ProductInfo>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <span
              style={{
                fontSize: produkt.product_name != null ? "25px" : "10px",
              }}
            >
              {produkt.product_name != null ? produkt.product_name : "."}
            </span>
            <span>
              {produkt.product_keywords.slice(0, 25)}
              {produkt.product_keywords.length > 25 ? "..." : ""}
            </span>
          </div>
          <span style={{ fontFamily: "serif" }}>
            {produkt.product_price.replace(",", ".")}â‚¬
          </span>
        </div>
        <div style={{ display: "flex" }}>
          <div>{produkt.product_rating}</div>
          <div
            style={{
              width: "18px",
              height: "18px",
              visibility: produkt.product_rating == null ? "hidden" : "shown",
            }}
          >
            <img
              src={startIcon}
              alt="starIcon"
              style={{ width: "100%", height: "100%" }}
            />
          </div>
        </div>
      </ProductInfo>
    </ProductDiv>
  );
}

export default SingleProd;
