import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { animated, useSpring, config } from "react-spring";
import leftArrow from "../../imgs/leftArrow.png";
import { easeSinInOut, easeSinIn, easeExpIn } from "d3-ease";

const LikeDiv = styled.div`
  width: 90%;
  height: 640px;
  display: flex;
  flex-direction: column;
  overflow-y: scroll;
  gap: 12px;
`;

const SingleLikeDiv = styled.div`
  align-items: center;
  display: flex;
  width: 100%;
  min-height: 165px;
  border: 1px solid black;
`;

const LikedImage = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 40%;
  height: 160px;
  background-color: white;
`;
const LikedInfo = styled.div`
  display: flex;
  width: 60%;
  height: 160px;
  border: 1px solid black;
`;

function LikeBar({ setOn, setSideLike }) {
  //console.log(likeList);
  var likeList = JSON.parse(localStorage.getItem("likes")).map(
    (prod, index) => {
      return prod[2];
    }
  );

  const [likedProd, setLikedProd] = useState(likeList);
  useEffect(() => {
    likeList = JSON.parse(localStorage.getItem("likes")).map((prod, index) => {
      return prod[2];
    });
    setLikedProd([...likeList]);
  }, [JSON.parse(localStorage.getItem("likes"))]);
  const [startStyle, setStartStyle] = useSpring(() => ({
    height: "800px",
    width: "0px",
    backgroundColor: "pink",
    position: "fixed",
    top: "30px",
    right: 0,
    borderLeft: "none",
    zIndex: 99,
    config: {
      duration: 500,
      tension: 1,
      mass: 1,
      friction: 0,
      easing: easeSinIn,
    },
  }));
  const [arrowStyle, setArrowStyle] = useSpring(() => ({
    width: "90px",
    height: "90px",
    cursor: "pointer",
    marginLeft: "20px",
    transform: "rotate(180deg)",
  }));
  if (setOn) {
    setStartStyle.stop();
    setStartStyle.start({ width: "600px" });
    setStartStyle.set({ borderLeft: "1px solid grey" });
  } else {
    setStartStyle.stop();
    setStartStyle.start({ width: "0px" });
    setStartStyle.set({ borderLeft: "none" });
  }
  return (
    <animated.div style={startStyle}>
      <div
        style={{
          display: "flex",
          width: "100%",
          height: "160px",
        }}
      >
        <animated.img
          src={leftArrow}
          alt="back"
          style={arrowStyle}
          onMouseOver={() => {
            setArrowStyle.stop();
            setArrowStyle.start({ marginLeft: "30px" });
          }}
          onMouseOut={() => {
            setArrowStyle.stop();
            setArrowStyle.start({ marginLeft: "20px" });
          }}
          onClick={() => {
            setSideLike();
          }}
        />
      </div>
      <LikeDiv>
        {likedProd.map((prod, index) => (
          <SingleLikeDiv>
            <LikedImage>
              <img
                src={prod.product_image}
                alt=""
                style={{ maxWidth: "100%", maxHeight: "100%" }}
              />
            </LikedImage>
            <LikedInfo>
              {prod.product_name != null ? (
                <h1>{prod.product_name}</h1>
              ) : (
                <h1>{prod.product_keywords.slice(0, 25)}</h1>
              )}
            </LikedInfo>
          </SingleLikeDiv>
        ))}
      </LikeDiv>
    </animated.div>
  );
}

export default LikeBar;
