import { createGlobalStyle } from "styled-components";
import MiamaWoff from "./fonts/Miama.woff";
import MiamaWoff2 from "./fonts/Miama.woff2";
import RobotoWoff from "./fonts/PlayfairDisplay.woff2";
import RobotoWoff2 from "./fonts/RobotoCondensed.woff2";

const FontStyles = createGlobalStyle`

@font-face {
  font-family: 'Miama';
  src: url(${MiamaWoff2}) format('woff2'),
       url(${MiamaWoff}) format('woff');
}
@font-face {
  font-family: 'Roboto';
  src: url(${RobotoWoff2}) format('woff2'),
       url(${RobotoWoff}) format('woff');
}
`;

export default FontStyles;
